import React, { useState } from 'react';
import { View, Text, Image, TextInput, TouchableOpacity, StyleSheet, Modal, ScrollView } from 'react-native';
import ChatBox from './Chatbox';

function HomePage() {
  const [showFilterModal, setShowFilterModal] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState(null);

  const toggleFilterModal = () => {
    setShowFilterModal(!showFilterModal);
  };

  const handleCategorySelection = (category) => {
    setSelectedCategory(category);
    toggleFilterModal();
    // Vous pouvez ajouter la logique de filtrage ici en fonction de la catégorie sélectionnée.
  };

  return (
    <View style={styles.container}>
      <View style={styles.header}>
        {/* Image de profil */}
        <Image
          source={require('../assets/images.png')}
          style={styles.profileImage}
        />

        {/* Message de bienvenue avec le nom de l'utilisateur */}
        <Text style={styles.welcomeText}>Bonjour, {'\n'} Wiem</Text>
      </View>

      {/* Barre de recherche */}
      <TextInput
        placeholder="Rechercher..."
        style={styles.searchBar}
      />

      {/* Section Historique */}
      <Text style={styles.historyText}></Text>

      {/* Modal de filtre */}
      <Modal
        visible={showFilterModal}
        animationType="slide"
        transparent={true}
        onRequestClose={toggleFilterModal}
      >
        <View style={styles.filterModal}>
          <Text style={styles.filterModalTitle}>Catégories :</Text>
          <ScrollView contentContainerStyle={styles.filterOptionsContainer}>
            <TouchableOpacity onPress={() => handleCategorySelection('Santé')}>
              <Text style={styles.filterOption}>Santé</Text>
            </TouchableOpacity>
            <TouchableOpacity onPress={() => handleCategorySelection('Maison')}>
              <Text style={styles.filterOption}>Maison</Text>
            </TouchableOpacity>
            <TouchableOpacity onPress={() => handleCategorySelection('Travail')}>
              <Text style={styles.filterOption}>Travail</Text>
            </TouchableOpacity>
          </ScrollView>

          <Text style={styles.filterModalTitle}>Keywords :</Text>
          <ScrollView contentContainerStyle={styles.filterOptionsContainer}>
            <TouchableOpacity onPress={() => handleCategorySelection('Convocation')}>
              <Text style={styles.filterOption}>Convocation</Text>
            </TouchableOpacity>
            <TouchableOpacity onPress={() => handleCategorySelection('Facture')}>
              <Text style={styles.filterOption}>Facture</Text>
            </TouchableOpacity>
            <TouchableOpacity onPress={() => handleCategorySelection('Ordonnance')}>
              <Text style={styles.filterOption}>Ordonnance</Text>
            </TouchableOpacity>
          </ScrollView>

          <TouchableOpacity style={styles.closeFilterModalButton} onPress={toggleFilterModal}>
            <Text style={styles.filterButtonText}>Fermer</Text>
          </TouchableOpacity>
        </View>
      </Modal>
      <ChatBox />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'flex-start',
    alignItems: 'center',
    padding: 70,
    backgroundColor: 'white',
  },
  header: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  profileImage: {
    width: 100,
    height: 100,
    borderRadius: 30,
    marginRight: 16,
  },
  welcomeText: {
    fontSize: 24,
    fontWeight: 'bold',
    marginTop: 10,
  },
  searchBar: {
    borderWidth: 1,
    borderColor: '#E5E5E5',
    padding: 12,
    marginTop: 20,
    width: '100%',
    borderRadius: 12,
  },
  filterButton: {
    backgroundColor: '#007AFF',
    padding: 12,
    borderRadius: 12,
    marginTop: 20,
    alignSelf: 'flex-end',
  },
  filterButtonText: {
    color: 'white',
    fontSize: 18,
  },
  historyText: {
    marginTop: 20,
    fontSize: 24,
    fontWeight: 'bold',
  },
  filterModal: {
    flex: 1,
    backgroundColor: 'white',
    padding: 16,
  },
  filterModalTitle: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 12,
  },
  filterOptionsContainer: {
    paddingBottom: 16,
  },
  filterOption: {
    fontSize: 18,
    marginVertical: 8,
    padding: 12,
    borderWidth: 1,
    borderColor: '#007AFF',
    borderRadius: 12,
    color: '#007AFF',
    fontWeight: 'bold',
    textAlign: 'center',
  },
  closeFilterModalButton: {
    backgroundColor: '#007AFF',
    padding: 12,
    borderRadius: 12,
    marginTop: 20,
    alignSelf: 'center',
  },
});

export default HomePage;
