import React from 'react';
import { View, Text, Image, StyleSheet } from 'react-native';
import { Switch,TouchableOpacity } from 'react-native';
import { Feather, FontAwesome5 } from '@expo/vector-icons';
import ChatBox from './Chatbox';


import { setDarkTheme, setLightTheme } from '../components/files/themeSlice';

import Constants from 'expo-constants';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { useAppDispatch, useAppSelector } from '../components/hooks/reduxHooks';

import { useNavigation } from '@react-navigation/native';
import { StackNavigationProp } from '@react-navigation/stack';

import { setSnack } from '../components/files/snackbarSlice';
import { SIZE } from '../components/utils/Constants';

function ProfileScreen() {
  // Supposons que vous ayez ces donnÃ©es du profil

  const { theme } = useAppSelector((state) => state.theme);
  const dispatch = useAppDispatch();

  return (
    <View
      style={[styles.container, { backgroundColor: theme.colors.background }]}
    >
      <View style={styles.section}>
        <View
          style={[
            styles.sectionItem,
            { backgroundColor: theme.colors.background2 },
          ]}
        >
        </View>
      </View>
      <View style={styles.section}>
        <Text style={[styles.sectionTitle, { color: theme.colors.primary }]}>
          Stockage
        </Text>
        <View
          style={[
            styles.sectionItem,
            { backgroundColor: theme.colors.background2 },
          ]}
        >
        </View>
        <View
          style={[
            styles.sectionItem,
            { backgroundColor: theme.colors.background2 },
          ]}
        >
        </View>
      </View>
      <ChatBox />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingTop: Constants.statusBarHeight + 20,
  },
  section: {
    width: SIZE,
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    marginBottom: 20,
  },
  sectionTitle: {
    fontFamily: 'Poppins_600SemiBold',
    fontSize: 16,
  },
  sectionItem: {
    display: 'flex',
    flexDirection: 'row',
    height: 45,
  },
  sectionItemText: {
    fontFamily: 'Poppins_500Medium',
  },
  sectionItemLeft: {
    width: '20%',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  },
  sectionItemCenter: {
    width: '60%',
    display: 'flex',
    alignItems: 'flex-start',
    justifyContent: 'center',
  },
  sectionItemRight: {
    width: '20%',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  },
});

export default ProfileScreen;
